def get_score(roll):
    count = {}
    for num in roll:
        if num in count:
            count[num] += 1
        else:
            count[num] = 1

    points = 0
    if len(count) == 6:
        points = 1000
    elif len(count) == 3 and all(value == 2 for value in count.values()):
        points = 750
    elif len(count) == 1:
        key = list(count.keys())[0]
        if key == 1:
            points = 1000 * 3
        else:
            points = 100 * key * 3
    else:
        for key, value in count.items():
            if value >= 3:
                if key == 1:
                    points += 1000
                else:
                    points += 100 * key
                if value > 3:
                    points *= value - 2
        if 1 in count:
            points += 100 * (count[1] % 3)
        if 5 in count:
            points += 50 * (count[5] % 3)

    if points == 0:
        return "Zonk"
    else:
        return points

# Take input from standard input
roll = list(map(int, input().split()))
# Call the function and print the output
print(get_score(roll))

