def is_palindrome(s):
    return s == s[::-1]

def can_form_palindrome(st):
    for i in range(len(st)):
        if st[i] != 'a':
            if chr(ord(st[i]) - 1) == 'a' or chr(ord(st[i]) + 1) == 'a':
                continue
            new_str = ''.join([chr(ord(st[j]) - 1) if j < i else chr(ord(st[j]) + 1) if j > i else st[j] for j in range(len(st))])
            if is_palindrome(new_str):
                return True
    return False

