def format_number(num):
    num_str = str(num)
    output = ""
    for i in range(len(num_str)):
        if num_str[i] != "0":
            output += num_str[i] + "*" + str(10 ** (len(num_str) - i - 1)) + "+" if i < len(num_str) - 1 else num_str[i] + "*" + str(10 ** (len(num_str) - i - 1))
        else:
            if "0" not in num_str[i:]:
                output += num_str[i] + "*" + str(10 ** (len(num_str) - i - 1)) + "+"
