def isSubstitutionCipher(string1, string2):
    def is_valid_cipher(cipher, s1, s2):
        mapping = {}
        for i in range(len(s1)):
            if s1[i] not in mapping:
                mapping[s1[i]] = s2[i]
            elif mapping[s1[i]] != s2[i]:
                return False
        return ''.join(cipher[i] for i in range(26) if chr(i + 97) in mapping) == ''.join(mapping[chr(i + 97)] for i in range(26))

    for i in range(26):
        for j in range(i + 1, 26):
            cipher = [chr(k + 97) for k in range(26)]
            cipher[i], cipher[j] = cipher[j], cipher[i]
            if is_valid_cipher(cipher, string1, string2):
                return True
    return False

