def alphabet_war(fight):
    left_power = {'w': 4, 'p': 3, 'b': 2, 's': 1}
    right_power = {'m': 4, 'q': 3, 'd': 2, 'z': 1}

    fight = list(fight)
    i = 0
    while i < len(fight):
        if fight[i] == '*':
            if i > 0 and fight[i-1] != '*':
                fight[i-1] = '_'
            if i < len(fight)-1 and fight[i+1] != '*':
                fight[i+1] = '_'
            fight[i] = '_'
        i += 1

    left_total = sum(left_power.get(letter, 0) for letter in fight if letter != '_')
    right_total = sum(right_power.get(letter, 0) for letter in fight if letter != '_')

    if left_total > right_total:
        return 'Left side wins!'
    elif right_total > left_total:
        return 'Right side wins!'
    else:
        return "Let's fight again!"

