def getAllPrimeFactors(n):
    if not isinstance(n, int) or n <= 0:
        return []
    if n == 0:
        return []
    if n == 1:
        return [1]
    if n == 2:
        return [2]

    factors = []
    divisor = 2
    while n > 1:
        while n % divisor == 0:
            factors.append(divisor)
            n //= divisor
        divisor += 1
    return factors

def getUniquePrimeFactorsWithCount(n):
    if not isinstance(n, int) or n <= 0:
        return [[], []]
    if n == 0:
        return [[], []]
    if n == 1:
        return [[1], [1]]
    if n == 2:
        return [[2], [1]]

    factors = getAllPrimeFactors(n)
    unique_factors = list(set(factors))
    counts = [factors.count(factor) for factor in unique_factors]
    return [unique_factors, counts]

def getUniquePrimeFactorsWithProducts(n):
    if not isinstance(n, int) or n <= 0:
        return []
    if n == 0:
        return []
    if n == 1:
        return [1]
    if n == 2:
        return [2]

    factors = getAllPrimeFactors(n)
    unique_factors = list(set(factors))
    powers = [factors.count(factor) for factor in unique_factors]
    products = [factor ** power for factor, power in zip(unique_factors, powers)]
    return products

n = int(input())
print(getAllPrimeFactors(n))
print(getUniquePrimeFactorsWithCount(n))
print(getUniquePrimeFactorsWithProducts(n))

