def play_with_string(s):
    vowels = "aeiou"
    result = ""
    for char in s:
        if char in "co":
            result += chr(ord(char) - 1)
        elif char == "d":
            result += chr(ord(char) - 3)
        elif char == "e":
            result += chr(ord(char) - 4)
        elif char in "aeiou":
            new_char = ord(char) - 5
            if new_char < ord('a'):
                new_char = ord('z') - (ord('a') - new_char - 1)
            result += chr(new_char)
        else:
            new_char = ord(char) + 9
            if new_char > ord('z'):
                new_char = ord('a') + (new_char - ord('z') - 1)
            result += chr(new_char)
    return result

