def modify_sentence(sentence):
    words = sentence.split()
    modified_words = []

    for word in words:
        if len(word.rstrip(',.')) > 6 or word.lower().count('t') >= 2:
            modified_words.append(word[::-1])
        elif len(word) == 2 or (word[-1] == ',' and len(word) > 1):
            modified_words.append(word.upper())
        elif len(word) == 1:
            modified_words.append('0')
        else:
            modified_words.append(word)

