def strictly_increasing_sequence(sequence):
    decreasing_count = 0
    for i in range(len(sequence) - 1):
        if sequence[i] >= sequence[i + 1]:
            decreasing_count += 1
            if decreasing_count > 1:
                return False
            if i == 0:
                return sequence[i] < sequence[i + 1]  # Check if removing the current element makes the sequence strictly increasing
            elif i == len(sequence) - 2:
                return sequence[i] < sequence[i + 1]  # Check if removing the next element makes the sequence strictly increasing
            else:
                if sequence[i] < sequence[i + 2] or sequence[i + 1] > sequence[i - 1]:
                    return True  # Check if removing the current or next element makes the sequence strictly increasing
                else:
                    return False
    return True

