def count_prime_primes(N):
    def sieve_of_eratosthenes(limit):
        primes = []
        sieve = [True] * (limit + 1)
        for num in range(2, limit + 1):
            if sieve[num]:
                primes.append(num)
                for multiple in range(num * num, limit + 1, num):
                    sieve[multiple] = False
        return primes

    def is_prime(num):
        if num < 2:
            return False
        for i in range(2, int(num ** 0.5) + 1):
            if num % i == 0:
                return False
        return True

    primes = sieve_of_eratosthenes(N)
    prime_primes_count = 0
    prime_primes_sum = 0

    for i in range(len(primes)):
        for j in range(i + 1, len(primes)):
            if primes[i] / primes[j] < 1 and is_prime(primes[i] / primes[j]):
                prime_primes_count += 1
                prime_primes_sum += primes[i] / primes[j]

    return [prime_primes_count, int(prime_primes_sum)]

