def count_ways_to_multiply(n, k):
    dp = [[0] * (n + 1) for _ in range(k + 1)]
    for i in range(1, k + 1):
        for j in range(1, n + 1):
            if i == 1:
                dp[i][j] = 1
            else:
                for x in range(1, j + 1):
                    dp[i][j] += dp[i - 1][j // x]
    return dp[k][n]

