def smallest_possible_sum(arr):
    n = len(arr)
    while True:
        arr.sort()
        if arr[0] == arr[n-1]:
            break
        for i in range(1, n):
            if arr[i] > arr[0]:
                arr[i] = arr[i] % arr[0]  # Modified to use modulo operation instead of subtraction
    return sum(arr)

