            "RCV_FIN": "CLOSING",
            "RCV_FIN_ACK": "TIME_WAIT",
            "RCV_ACK": "FIN_WAIT_2"
        },
        "CLOSING": {
            "RCV_ACK": "TIME_WAIT"
        },
        "FIN_WAIT_2": {
            "RCV_FIN": "TIME_WAIT"
        },
        "TIME_WAIT": {
            "APP_TIMEOUT": "CLOSED"
        },
        "CLOSE_WAIT": {
            "APP_CLOSE": "LAST_ACK"
        },
        "LAST_ACK": {
            "RCV_ACK": "CLOSED"
