def simplify(s):
    monomials = s.replace('-', '- ').replace('+', ' +').split()
    monomials.sort(key=lambda x: (sum(c.isalpha() for c in x), x))
    result = {}
    for monomial in monomials:
        sign = -1 if monomial[0] == '-' else 1
        monomial = monomial.lstrip('+-')
        if monomial in result:
            result[monomial] += sign
        else:
            result[monomial] = sign
    simplified = ''.join(f'{sign}{monomial}' for monomial, sign in sorted(result.items()) if sign != 0)
    if simplified and simplified[0] == '+':
        simplified = simplified[1:]
    return simplified

