def count_frequency(s):
    freq = {}
    for char in s:
        if char.islower():
            freq[char] = freq.get(char, 0) + 1
    return freq

def mix(s1, s2):
    freq1 = count_frequency(s1)
    freq2 = count_frequency(s2)

    result = []
