def differentiate(polynomial, x):
    terms = polynomial.split('+')
    result = 0
    for term in terms:
        if 'x' in term:
            coefficient, exponent = term.split('x')
            if coefficient == '':
                coefficient = '1'
            elif coefficient == '-':
                coefficient = '-1'
            else:
                coefficient = int(coefficient)
            if exponent == '':
                exponent = '1'
            else:
                exponent = int(exponent[1:])
            if exponent == 0:
                result += 0
            else:
                result += coefficient * exponent * (x ** (exponent - 1))
    return result

