def min_sum_of_money(n, m, languages):
    edges = []
    for i in range(n):
        for j in range(i+1, n):
            common_languages = set(languages[i][1:]) & set(languages[j][1:])
            cost = len(common_languages)
            edges.append((i, j, cost))

    edges.sort(key=lambda x: x[2])

    parent = list(range(n))

    def find(x):
        if parent[x] != x:
            parent[x] = find(parent[x])
        return parent[x]

    def union(x, y):
        px, py = find(x), find(y)
        if px != py:
            parent[py] = px

    total_cost = 0
    for edge in edges:
        u, v, cost = edge
        if find(u) != find(v):
            union(u, v)
            total_cost += cost

    return total_cost

