def berzerk_game(n, rick_set, morty_set):
    def can_win(start, moves):
        dp = [0] * (n + 1)
        for i in range(1, n):
            for move in moves:
                if i + move <= n:
                    dp[i + move] = 1
                else:
                    dp[i + move - n] = 1
        return dp[start]

    rick_outcome = []
    morty_outcome = []

    for i in range(1, n):
        rick_outcome.append("Win" if can_win(i, rick_set) else "Lose" if can_win(i, morty_set) else "Loop")
        morty_outcome.append("Win" if can_win(i, morty_set) else "Lose" if can_win(i, rick_set) else "Loop")

    return " ".join(rick_outcome), " ".join(morty_outcome)

