from collections import deque

def add_edge(u, v, adj):
    adj[u].append(v)
    adj[v].append(u)

def find_centers(n, adj):
    degree = [0] * n
    leaves = deque()
    for i in range(n):
        degree[i] = len(adj[i])
        if degree[i] == 1:
            leaves.append(i)

    remaining_nodes = n
    while remaining_nodes > 2:
        remaining_nodes -= len(leaves)
        new_leaves = deque()
        while leaves:
            leaf = leaves.popleft()
            for neighbor in adj[leaf]:
                degree[neighbor] -= 1
                if degree[neighbor] == 1:
                    new_leaves.append(neighbor)
        leaves = new_leaves

    return list(leaves)

def make_sequence_valid(n, a, adj):
    centers = find_centers(n, adj)
    if len(centers) == 1:
        center = centers[0]
        for i in range(n):
            if len(adj[i]) == 1:
                a[center] = i + 1
                break
    else:
        for center in centers:
            if a[center] == center + 1:
                a[center] = (center + 1) % n + 1
                break
        if a[centers[0]] == centers[0] + 1:
            a[centers[0]] = (centers[0] + 1) % n + 1
        else:
            a[centers[1]] = (centers[1] + 1) % n + 1
    return len(centers), a

def main():
    n = int(input())
    a = list(map(int, input().split()))
    adj = [[] for _ in range(n)]
    for i in range(n):
        add_edge(i, a[i] - 1, adj)

    changes, updated_sequence = make_sequence_valid(n, a, adj)
    print(changes)
    print(*updated_sequence)

main()

