def can_reorder_boxes(k, boxes):
    def backtrack(boxes, target_sum, current_sum, path, visited):
        if len(path) == k:
            return all(sum(box) == target_sum for box in boxes)

        for i in range(k):
            if not visited[i]:
                for num in boxes[i]:
                    visited[i] = True
                    path.append((num, i+1))
                    current_sum += num
                    if current_sum == target_sum:
                        if backtrack(boxes, target_sum, 0, path, visited):
                            return True
                    elif current_sum < target_sum:
                        if backtrack(boxes, target_sum, current_sum, path, visited):
                            return True
                    path.pop()
                    visited[i] = False
                    current_sum -= num
        return False

    total_sum = sum(sum(box) for box in boxes)
    if total_sum % k != 0:
        return "No"

    target_sum = total_sum // k
    visited = [False] * k
    path = []

    if backtrack(boxes, target_sum, 0, path, visited):
        result = ["Yes"]
        for pick in path:
            result.append(f"{pick[0]} {pick[1]}")
        return "\n".join(result)
    else:
        return "No"

