def is_valid_move(grid, car, r, c):
    if r < 0 or r >= 4 or c < 0 or c >= len(grid[0]):
        return False
    if grid[r][c] != 0:
        return False
    if r == 0 and grid[r+1][c] == car:
        return False
    if r == 3 and grid[r-1][c] == car:
        return False
    return True

def move_car(grid, car, r, c, moves, max_moves, visited):
    if moves > max_moves:
        return False, []
    if r == 0 or r == 3:
        if grid[r][c] == car:
            return True, [(car, r+1, c+1)]
        else:
            return False, []
    for dr, dc in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
        new_r, new_c = r + dr, c + dc
        if is_valid_move(grid, car, new_r, new_c) and (new_r, new_c) not in visited:
            grid[new_r][new_c] = car
            grid[r][c] = 0
            visited.add((new_r, new_c))
            valid, next_moves = move_car(grid, car, new_r, new_c, moves+1, max_moves, visited)
            if valid:
                return True, [(car, new_r+1, new_c+1)] + next_moves
            grid[new_r][new_c] = 0
            grid[r][c] = car
            visited.remove((new_r, new_c))
    return False, []

def parking_cars(n, k, grid):
    moves = []
    for i in range(1, k+1):
        for r in range(1, 3):
            for c in range(n):
                if grid[r][c] == i:
                    valid, car_moves = move_car([row[:] for row in grid], i, r, c, 0, 20000, set())
                    if valid:
                        moves += car_moves
                    else:
                        return -1
    return len(moves), moves

n, k = map(int, input().split())
grid = [list(map(int, input().split())) for _ in range(4)]
result = parking_cars(n, k, grid)
if result == -1:
    print(-1)
else:
    print(result[0])
    for move in result[1]:
        print(*move)

