def max_interest_value(n, T, tasks):
    dp = [[0] * (T + 1) for _ in range(n + 1)]

    for i in range(1, n + 1):
        for j in range(1, T + 1):
            if tasks[i-1][0] > j:
                dp[i][j] = dp[i-1][j]
            else:
                dp[i][j] = max(dp[i-1][j], tasks[i-1][1] + dp[i-1][j - tasks[i-1][0]])

    return dp[n][T]

