def fish_weight_comparison(n, m, k, alice_fish, bob_fish):
    fish_count = {}
    for fish in alice_fish:
        if fish in fish_count:
            fish_count[fish] += 1
        else:
            fish_count[fish] = 1
    for fish in bob_fish:
        if fish in fish_count:
            fish_count[fish] -= 1
        else:
            fish_count[fish] = -1
    for count in fish_count.values():
        if count > 0:
            return "YES"
    return "NO"

