import heapq

def dijkstra(adj_list, start):
    n = len(adj_list)
    dist = [float('inf')] * n
    dist[start] = 0
    pq = [(0, start)]

    while pq:
        d, u = heapq.heappop(pq)
        if d > dist[u]:
            continue
        for v, w in adj_list[u]:
            if dist[u] + w < dist[v]:
                dist[v] = dist[u] + w
                heapq.heappush(pq, (dist[v], v))

    return dist

def main():
    n, m, a, b = map(int, input().split())
    adj_list = [[] for _ in range(n)]

    for _ in range(m):
        u, v, c = map(int, input().split())
        adj_list[u-1].append((v-1, c))
        adj_list[v-1].append((u-1, c))

    dist_from_1 = dijkstra(adj_list, 0)

    for p in range(n):
        min_time = dist_from_1[p]
        for u in range(n):
            for v, c in adj_list[u]:
                if (dist_from_1[u] < dist_from_1[p] and dist_from_1[v] < dist_from_1[p]) or (dist_from_1[u] > dist_from_1[p] and dist_from_1[v] > dist_from_1[p]):
                    min_time = min(min_time, dist_from_1[u] + dist_from_1[v] + min(a, b))
        print(min_time, end=" ")

main()

