def find_minimal_groups(n, words):
    def replace(word):
        while "u" in word:
            word = word.replace("u", "oo")
        while "h" in word:
            word = word.replace("h", "kh")
        return word

    distinct_names = set()
    for word in words:
        replaced_word = replace(word)
        distinct_names.add(replaced_word)

    return len(distinct_names)

