def minCostToMakeEqual(a, b):
    cost = 0
    diff_indices = []
    for i in range(len(a)):
        if a[i] != b[i]:
            diff_indices.append(i)

    for i in range(len(diff_indices) - 1):
        if diff_indices[i+1] - diff_indices[i] == 1:
            cost += 1
            diff_indices[i+1] = -1

    for i in range(len(diff_indices) - 1):
        if diff_indices[i+1] != -1:
            cost += abs(diff_indices[i+1] - diff_indices[i])

    return cost

