def min_energy_spent(n, l, r, Ql, Qr, weights):
    total_energy = 0
    left = 0
    right = n - 1
    while left <= right:
        if weights[left] <= weights[right]:
            total_energy += weights[left] * l
            if left != right:
                total_energy += Ql
            left += 1
        else:
            total_energy += weights[right] * r
            if left != right:
                total_energy += Qr
            right -= 1
    return total_energy

