def count_paths(n, m, labyrinth):
    MOD = 10**9 + 7
    dp = [[0] * (m+1) for _ in range(n+1)]
    dp[1][1] = 1

    for i in range(1, n+1):
        for j in range(1, m+1):
            if labyrinth[i-1][j-1] == 'R':
                dp[i][j] = 0
            else:
                dp[i][j] = (dp[i-1][j] + dp[i][j-1]) % MOD

    return dp[n][m] % MOD

# Read input from standard input
