def alphabet_war(fight):
    power_left = {'w': 4, 'p': 3, 'b': 2, 's': 1}
    power_right = {'m': 4, 'q': 3, 'd': 2, 'z': 1}
    fight = list(fight)

    # Explode bombs
    for i in range(len(fight)):
        if fight[i] == '*':
            for j in range(max(0, i-1), min(len(fight), i+2)):
                fight[j] = '_'

    # Calculate the power
    left_score = sum(power_left.get(char, 0) for char in fight)
    right_score = sum(power_right.get(char, 0) for char in fight)

    # Determine the winner
    if left_score > right_score:
        return "Left side wins!"
    elif right_score > left_score:
        return "Right side wins!"
    else:
        return "Let's fight again!"

# Read input and call the function
