def battle(player1, player2):
    survivors = {'player1': [], 'player2': []}
    for i in range(min(len(player1), len(player2))):
        p1 = player1[i]
        p2 = player2[i]
        if p1[0] >= p2[1]:
            p2[1] = 0
        else:
            p2[1] -= p1[0]
        if p2[0] >= p1[1]:
            p1[1] = 0
        else:
            p1[1] -= p2[0]
        if p1[1] > 0:
            survivors['player1'].append(p1)
        if p2[1] > 0:
            survivors['player2'].append(p2)
    if len(player1) > len(player2):
        survivors['player1'].extend(player1[len(player2):])
    elif len(player2) > len(player1):
        survivors['player2'].extend(player2[len(player1):])
    return survivors

