def getAllPrimeFactors(n):
    if not isinstance(n, int) or n <= 0:
        return []
    if n == 1:
        return [1]
    factors = []
    divisor = 2
    while n > 1:
        while n % divisor == 0:
            factors.append(divisor)
            n //= divisor
        divisor += 1
    return factors

def getUniquePrimeFactorsWithCount(n):
    factors = getAllPrimeFactors(n)
    if not factors:
        return [[], []]
    primes = []
    counts = []
    for factor in factors:
        if factor not in primes:
            primes.append(factor)
            counts.append(factors.count(factor))
    return [primes, counts]

def getUniquePrimeFactorsWithProducts(n):
    unique_factors = getUniquePrimeFactorsWithCount(n)
    if not unique_factors[0]:
        return []
    return [p**c for p, c in zip(*unique_factors)]

n = int(input())
print(getAllPrimeFactors(n))
print(getUniquePrimeFactorsWithCount(n))
print(getUniquePrimeFactorsWithProducts(n))

