def play_with_string(s):
    vowels = 'aeiou'
    consonants = 'bcdfghjklmnpqrstvwxyz'
    result = ''
    for char in s:
        if char in vowels:
            # Move vowels back 5 places
            result += chr((ord(char) - ord('a') - 5) % 26 + ord('a'))
        elif char in consonants:
            # Move consonants forward 9 places
            result += chr((ord(char) - ord('a') + 9) % 26 + ord('a'))
        else:
            result += char

        # Handle exceptions
        if char == 'c' or char == 'o':
            result = result[:-1] + chr(ord(char) - 1)
        elif char == 'd':
            result = result[:-1] + chr(ord(char) - 3)
        elif char == 'e':
            result = result[:-1] + chr(ord(char) - 4)

        # Revert if moved letter becomes 'c', 'o', 'd', or 'e'
        if result[-1] in 'code':
            result = result[:-1] + char

    return result

# Read input and call the function
s = input().strip()
print(play_with_string(s))

