def modify_sentence(sentence):
    words = sentence.split(' ')
    modified_words = []

    for word in words:
        # Check for transformations
        if len(word.rstrip('.,!?')) > 6 or word.lower().count('t') >= 2:
            # Reverse the word but keep punctuation at the end
            punctuation = word[-1] if word[-1] in '.,!?' else ''
            modified_word = word.rstrip('.,!?')[::-1] + punctuation
        elif len(word) == 2 or (len(word) > 2 and word[-1] == ','):
            modified_word = word.upper()
        elif len(word) == 1:
            modified_word = '0'
        else:
            modified_word = word
        modified_words.append(modified_word)

    return ' '.join(modified_words)

