def replace_yellow_color(color):
    yellow_to_green = {
        "gold": "ForestGreen",
        "khaki": "LimeGreen",
        "lemonchiffon": "PaleGreen",
        "lightgoldenrodyellow": "SpringGreen",
        "lightyellow": "MintCream",
        "palegoldenrod": "LightGreen",
        "yellow": "Lime"
    }

    color_lower = color.lower()
    if color_lower in yellow_to_green:
        return yellow_to_green[color_lower]

    if color.startswith('#') and len(color) == 7:
        r, g, b = int(color[1:3], 16), int(color[3:5], 16), int(color[5:], 16)
        if r > b and g > b:
            new_r, new_g, new_b = sorted([r, g, b], reverse=True)
            new_color = f"#{new_b:02X}{new_r:02X}{new_g:02X}"
            return new_color.upper() if color.isupper() else new_color.lower()

    return color

input_color = input()
print(replace_yellow_color(input_color))

def sieve_of_eratosthenes(limit):
    sieve = [True] * (limit + 1)
    sieve[0] = sieve[1] = False
    for i in range(2, int(limit**0.5) + 1):
        if sieve[i]:
            for j in range(i*i, limit + 1, i):
                sieve[j] = False
    return [i for i in range(limit + 1) if sieve[i]]

def sum_of_dominant_primes(a, b):
    primes = sieve_of_eratosthenes(b)
    prime_positions = sieve_of_eratosthenes(len(primes) - 1)
    sum_dominant_primes = 0
    for i in prime_positions:
        if i < len(primes) and a <= primes[i] <= b:
            sum_dominant_primes += primes[i]
    return sum_dominant_primes

# Main execution
if __name__ == "__main__":
    a, b = map(int, input().split())
    print(sum_of_dominant_primes(a, b))

