def almost_everywhere_zero(n, k):
    str_n = str(n)
    len_n = len(str_n)
    dp = [[0 for _ in range(k+1)] for _ in range(len_n+1)]
    dp[0][0] = 1

    for i in range(1, len_n+1):
        for j in range(k+1):
            if j == 0:
                dp[i][j] = 1
            else:
                # Counting numbers with all digits
                dp[i][j] += dp[i-1][j-1] * 9
                # Counting numbers with leading zeros
                if str_n[i-1] != '0':
                    dp[i][j] += dp[i-1][j]
                # Counting numbers with the current digit
                if j > 0 and str_n[i-1] != '0':
                    dp[i][j] += dp[i-1][j-1] * (int(str_n[i-1]) - 1)
                # Counting the number itself if it has non-zero digits equal to k
                if j == 1 and str_n[i-1] != '0' and i == len_n:
                    dp[i][j] += 1

    return dp[len_n][k]

