def mix(s1, s2):
    freq_s1 = {char: s1.count(char) for char in set(s1) if char.islower()}
    freq_s2 = {char: s2.count(char) for char in set(s2) if char.islower()}

    differences = []

    for char in set(freq_s1.keys()).union(freq_s2.keys()):
        count_s1 = freq_s1.get(char, 0)
        count_s2 = freq_s2.get(char, 0)
        if count_s1 > count_s2 and count_s1 > 1:
            differences.append(f"1:{char * count_s1}")
        elif count_s2 > count_s1 and count_s2 > 1:
            differences.append(f"2:{char * count_s2}")
        elif count_s1 == count_s2 and count_s1 > 1:
            differences.append(f"=:{char * count_s1}")

    differences.sort(key=lambda x: (-len(x), x))

    return '/'.join(differences)

