from collections import defaultdict
import re

def simplify(poly):
    terms = re.findall(r'[+-]?[^+-]+', poly)
    parsed_terms = defaultdict(int)

    for term in terms:
        if '*' in term:
            coeff, var = term.split('*')
        else:
            coeff = ''.join(filter(lambda x: x.isdigit() or x == '-', term))
            var = ''.join(filter(lambda x: x.isalpha(), term))
        coeff = int(coeff) if coeff else 1
        var = ''.join(sorted(var))
        parsed_terms[var] += coeff

    result_terms = []
    for var in sorted(parsed_terms, key=lambda x: (len(x), x)):
        coeff = parsed_terms[var]
        if coeff == 0:
            continue
        if coeff == -1:
            result_terms.append('-' + var)
        elif coeff == 1:
            result_terms.append('+' + var)
        else:
            result_terms.append(f'{coeff:+d}{var}')

    result = ''.join(result_terms)
    if result.startswith('+'):
        result = result[1:]
    return result

