def next_bigger(n):
    num = list(str(n))
    i = len(num) - 2
    while i >= 0 and num[i] >= num[i + 1]:
        i -= 1
    if i == -1:
        return -1
    j = len(num) - 1
    while num[j] <= num[i]:
        j -= 1
    num[i], num[j] = num[j], num[i]
    num[i + 1:] = reversed(num[i + 1:])
    return int(''.join(num))

