def simplify(poly):
    terms = re.findall(r'[+-]?[^+-]+', poly)
    simplified = defaultdict(int)
    for term in terms:
        num = ''.join(filter(str.isdigit, term))
        num = int(num) if num else 1
        var = ''.join(sorted(filter(str.isalpha, term)))
        if term[0] == '-':
            num *= -1
        simplified[var] += num
    result = []
    for var in sorted(simplified, key=lambda v: (len(v), v)):
        num = simplified[var]
        if num == 0:
            continue
        sign = '+' if num > 0 else ''
        if num == 1 or num == -1:
            num = '' if num > 0 else '-'
        result.append(f'{sign}{num}{var}')
    return ''.join(result).lstrip('+')

