def count_paths(n, m, labyrinth):
    # Initialize a 2D array to store the number of paths to each cell
    dp = [[0] * m for _ in range(n)]
    dp[0][0] = 1  # Start cell

    # Precompute the rightmost and bottommost cells that can be reached without pushing a rock
    rightmost = [[0] * m for _ in range(n)]
    bottommost = [[0] * m for _ in range(n)]
    for i in range(n):
        for j in range(m):
            if labyrinth[i][j] == 'R':
                rightmost[i][j] = j
                bottommost[i][j] = i
            else:
                if i > 0:
                    bottommost[i][j] = bottommost[i-1][j]
                else:
                    bottommost[i][j] = -1
                if j > 0:
                    rightmost[i][j] = rightmost[i][j-1]
                else:
                    rightmost[i][j] = -1

    # Calculate the number of paths using dynamic programming
    for i in range(n):
        for j in range(m):
            if i > 0 and j > rightmost[i-1][j]:
                dp[i][j] += dp[i-1][j]
            if j > 0 and i > bottommost[i][j-1]:
                dp[i][j] += dp[i][j-1]
            dp[i][j] %= MOD

    return dp[n-1][m-1]

