def min_cost_to_learn_languages(n, m, employees):
    languages = [set() for _ in range(m)]
    for i, langs in enumerate(employees):
        for lang in langs:
            languages[lang-1].add(i)

    visited = [False] * n
    def dfs(employee):
        visited[employee] = True
        for lang in employees[employee]:
            for colleague in languages[lang-1]:
                if not visited[colleague]:
                    dfs(colleague)

    components = 0
    for employee in range(n):
        if not visited[employee]:
            dfs(employee)
            components += 1

    return components - 1 if any(employees) else n

