def maze_game(n, q, queries):
    blocked = set()
    for r, c in queries:
        if (r, c) in blocked:
            blocked.remove((r, c))
        else:
            blocked.add((r, c))

        # Check if there is a blockage in the path
        if ((1, c) in blocked and (2, c) in blocked) or \
           ((1, c-1) in blocked and (1, c) in blocked) or \
           ((2, c-1) in blocked and (2, c) in blocked):
            yield "No"
        else:
            yield "Yes"

