def max_photos(n, a, b, T, orientation):
    time = 1 if orientation[0] == 'h' else 1 + b
    if time > T:
        return 0

    photos = 1
    left_time = right_time = time
    left_photos = right_photos = 0

    # Calculate time and photos to the right
    for i in range(1, n):
        right_time += a + 1
        if orientation[i] == 'w':
            right_time += b
        if right_time <= T:
            right_photos += 1
        else:
            break

    # Calculate time and photos to the left
    for i in range(n-1, 0, -1):
        left_time += a + 1
        if orientation[i] == 'w':
            left_time += b
        if left_time <= T:
            left_photos += 1
        else:
            break

    return photos + max(left_photos, right_photos)

