from collections import defaultdict
import heapq

def max_happiness(n, k, edges):
    graph = defaultdict(list)
    for u, v in edges:
        graph[u].append(v)
        graph[v].append(u)

    def dfs(node, parent):
        size = 1
        depth = 0
        for child in graph[node]:
            if child != parent:
                child_size, child_depth = dfs(child, node)
                size += child_size
                depth += child_depth
        return size, depth + size - 1

    _, depth_sum = dfs(1, -1)
    priority_queue = [(0, 1, -1)]  # (gain, node, parent)
    while len(priority_queue) < k:
        gain, node, parent = heapq.heappop(priority_queue)
        for child in graph[node]:
            if child != parent:
                child_size, _ = dfs(child, node)
                heapq.heappush(priority_queue, (gain + child_size, child, node))

    return depth_sum + sum(gain for gain, _, _ in priority_queue)

