    total_sum = sum(sum(box) for box in boxes)
    if total_sum % k != 0:
        return "No"
    target_sum = total_sum // k
    current_sums = [sum(box) for box in boxes]
    moves = []
    for i in range(k):
        if current_sums[i] < target_sum:
            for j in range(k):
                if current_sums[j] > target_sum:
                    diff = min(boxes[j], key=lambda x: abs((current_sums[i] + x) - target_sum))
                    moves.append((diff, i + 1))
                    current_sums[i] += diff
                    current_sums[j] -= diff
                    boxes[j].remove(diff)
                    boxes[i].append(diff)
                    break
    if all(sum == target_sum for sum in current_sums):
        return "Yes\n" + "\n".join(f"{c} {p}" for c, p in moves)
    else:
        return "No"

