def max_train_routes_to_close(n, m, k, roads, trains):
    from heapq import heappush, heappop

    # Create an adjacency list for the graph
    graph = {i: [] for i in range(1, n + 1)}
    for u, v, x in roads:
        graph[u].append((v, x))
        graph[v].append((u, x))

    # Dijkstra's algorithm to find the shortest path from the capital to all cities
    def dijkstra(start):
        distances = {city: float('inf') for city in graph}
        distances[start] = 0
        queue = [(0, start)]

        while queue:
            current_distance, current_city = heappop(queue)
            if current_distance > distances[current_city]:
                continue

            for neighbor, weight in graph[current_city]:
                distance = current_distance + weight
                if distance < distances[neighbor]:
                    distances[neighbor] = distance
                    heappush(queue, (distance, neighbor))

        return distances

    # Get the shortest distances from the capital to all cities
    shortest_distances = dijkstra(1)

    # Count the number of train routes that can be closed
    routes_to_close = 0
    for s, y in trains:
        if y > shortest_distances[s]:
            routes_to_close += 1

    return routes_to_close

