def max_groups(n):
    primes = []
    for i in range(2, n+1):
        is_prime = True
        for p in primes:
            if i % p == 0:
                is_prime = False
                break
        if is_prime:
            primes.append(i)

    used = [False] * (n+1)
    groups = []

    # Pair up multiples of each prime number
    for p in primes:
        multiples = []
        for i in range(p, n+1, p):
            if not used[i]:
                multiples.append(i)
        for i in range(0, len(multiples) - len(multiples) % 2, 2):
            groups.append((multiples[i], multiples[i+1]))
            used[multiples[i]] = True
            used[multiples[i+1]] = True

    # Pair up remaining numbers with their smallest prime factor
    for i in range(3, n+1, 2):
        if not used[i]:
            for p in primes:
                if i % p == 0 and not used[p] and p != i:
                    groups.append((i, p))
                    used[i] = True
                    used[p] = True
                    break

    return groups

