def parking_lot(n, k, grid):
    moves = []
    for i in range(1, k+1):
        # Find the initial position of car i
        for r in range(1, 5):
            if i in grid[r-1]:
                c = grid[r-1].index(i) + 1
                if r == 2:
                    # Move car i up to its parking spot
                    while r > 1:
                        moves.append((i, r-1, c))
                        r -= 1
                elif r == 3:
                    # Move car i down to its parking spot
                    while r < 4:
                        moves.append((i, r+1, c))
                        r += 1
                break
        # Check if the number of moves exceeds the limit
        if len(moves) > 20000:
            return -1
    return moves

