def max_accordion_length(s):
    n = len(s)
    ind = -1
    bind = -1
    f = 0
    for i in range(n):
        if s[i] == '[':
            f = 1
        if s[i] == ':' and f == 1:
            ind = i
            break
    f = 0
    for i in range(n-1, -1, -1):
        if s[i] == ']':
            f = 1
        if s[i] == ':' and f == 1:
            bind = i
            break
    if ind == -1 or bind == -1 or ind >= bind:
        return -1
    else:
        ans = 4
        for i in range(ind, bind):
            if s[i] == '|':
                ans += 1
        return ans

s = input()
print(max_accordion_length(s))

def max_sum_of_digits(x):
    num = list(str(x))
    best = num[:]
    for i in range(len(num)-1, -1, -1):
        if num[i] == '0':
            continue
        num[i] = str(int(num[i]) - 1)
        num[i+1:] = ['9'] * (len(num) - i - 1)
        if sum(map(int, num)) >= sum(map(int, best)):
            best = num[:]
    return int(''.join(best))

