def fish_weight_comparison(n, m, k, alice_fish, bob_fish):
    alice_fish.sort(reverse=True)
    bob_fish.sort(reverse=True)
    for i in range(min(n, m)):
        if alice_fish[i] > bob_fish[i]:
            return "YES"
        elif alice_fish[i] < bob_fish[i]:
            return "NO"
    if n > m:
        return "YES"
    return "NO"

